//******************************************************************************// Title:       Plane3D.cpp// Author:      Maarten Gribnau// Date:        07-04-1995// Purpose:     Implementation of Plane3D class.// Descrption: // Project:     Graph3D// Remarks:     //// Revision history:// Who:// Date:// What://******************************************************************************#include "Plane3D.hpp"#include <iostream.h>#include "Macros3D.h"#include "Line3D.hpp"//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Plane3D//|//| Purpose: Default constructor//|//| Parameters: none//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Plane3D::Plane3D(void){  normal.Set(REAL_ONE, 0, 0);  offset = 0;}  //==== Plane3D::Plane3D() ====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Plane3D//|//| Purpose: This method creates a Plane3D from normal and offset//|//| Parameters: theNormal: normal of plane//|             theOffset: offset of plane//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Plane3D::Plane3D(const Vector3D &theNormal, Real theOffset){   normal = theNormal;  offset = theOffset;  }  //==== Plane3D::Plane3D() ====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Plane3D//|//| Purpose: This method creates a Plane3D by copying another//|//| Parameters: thePlane: Plane3D to copy//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Plane3D::Plane3D(const Plane3D *thePlane){  normal = thePlane->normal;  offset = thePlane->offset;  } //==== Plane3D::Plane3D() ====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Plane3D//|//| Purpose: This method creates a Plane3D by copying another//|//| Parameters: theNormal: Plane3D to copy//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Plane3D::Plane3D(const Plane3D &thePlane){  normal = thePlane.normal;  offset = thePlane.offset;}  //==== Plane3D::Plane3D() ====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Plane3D//|//| Purpose: This method creates a Plane3D from three points in space//|//| Parameters: point1, point2, point3: Span the plane//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Plane3D::Plane3D(const Vector3D &point1,                 const Vector3D &point2,                 const Vector3D &point3){  Set(point1, point2, point3);}  //==== Plane3D::Plane3D() ====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Plane3D//|//| Purpose: This method creates a Plane3D from normal and point on the//|          plane//|//| Parameters: theNormal: normal of the new plane//|             point:     point on the plane//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Plane3D::Plane3D(const Vector3D &theNormal, const Vector3D &point){  normal = theNormal;  offset = CalculateOffset(point);}  //==== Plane3D::Plane3D() ====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::~Plane3D//|//| Purpose: This method destroys a Plane3D//|//| Parameters: none//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Plane3D::~Plane3D(void){}  //====Plane3D::~Plane3D====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Intersect//|//| Purpose: This calculates calculates this plane with the line given.//|          Graphics Gems page 299//|//| Parameters: line:      line to intersect with//|             parameter: value of parameter of this line at intersection//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Flag Plane3D::Intersect(const Line3D &line, Real &parameter){  Real dot1 = normal.Dot(line.Origin());  Real dot2 = normal.Dot(line.Direction());  if (dot2 == 0)    return FALSE;   parameter = dot1 + offset;  parameter = REAL_DIV(parameter, dot2);  parameter = -parameter;  return TRUE;  } //==== Plane3D::Intersect====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Intersect//|//| Purpose: This calculates intersection ofthis plane with the line//|          given.//|//| Parameters: line:  line to intersect with//|             point: intersection//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Flag Plane3D::Intersect(const Line3D &line, Vector3D &point){  Real parameter;  if (Intersect(line, parameter) == FALSE)    return FALSE;  line.ParameterToPoint(parameter, point);  return TRUE;	} //==== Plane3D::Intersect====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Project//|//| Purpose: Finds nearest point on plane to inPoint.//|          See Graphic Gems page 297.//|//| Parameters: inPoint:  point to project//|             outPoint: inPoint projected onto the plane//|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________void Plane3D::Project(const Vector3D &inPoint, Vector3D &outPoint){  Real k;    k  = normal.Dot(inPoint);  k += offset;  k  = REAL_DIV(k, normal.Dot(normal));  outPoint += inPoint - (outPoint * k);  } //==== Plane3D::Project====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Plane3D::Distance//|//| Purpose: Finds distance from point to plane.//|          See Graphic Gems page 297.//|//| Parameters: point: input for distance calculations //|//| Created by Maarten Gribnau//|//| Modified://|___________________________________________________________________Real Plane3D::Distance(const Vector3D &point){  Vector3D projected;    Project(point, projected);  return projected.Distance(point);  } //==== Plane3D::Distance====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Transform//|//| Purpose: This applies a transformation to this plane.//|//| Parameters: M:      the matrix of the transformation//|             result: the plane to put the result in//|//| Created by Maarten Gribnau//|___________________________________________________________________void Plane3D::Transform(const Matrix3D &M){  normal.TransformDirection(M);  // Get a point on the plane  Vector3D point(normal);  point *= offset;  point *= M;  offset = CalculateOffset(point);    }  //====Plane3D::Transform====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| TransformTo//|//| Purpose: This applies a transformation to this plane, and puts//|          the result in another plane//|//| Parameters: M:      the matrix of the transformation//|             result: the plane to put the result in//|//| Created by Maarten Gribnau//|___________________________________________________________________void Plane3D::TransformTo(const Matrix3D &M, Plane3D &result){  result.normal = normal.TransformDirectionTo(M);  Vector3D point(normal);  point *= offset;  point *= M;  result.offset = CalculateOffset(point);    }  //====Plane3D::TransformTo====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Operator << for Plane3D//| //| Purpose: This operator outputs a Plane3D to an output stream//|//| Parameters: o: the output stream//|             p: the plane to output//|//| Created by Maarten Gribnau//| //| Modified://|________________________________________________________________ostream &operator << (ostream &o, const Plane3D& p){  o << "planeNormal: " << p.normal << '\n';  o << "planeOffset: " << p.offset << '\n';  o << '\n';  return o;}  //==== operator << for Plane3D ====//|~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~//| Operator >> for Plane3D//| //| Purpose: This operator inputs a Plane3D from an input stream//|//| Parameters: i: the output stream//|             p: the plane//|//| Created by Maarten Gribnau//| //| Modified://|________________________________________________________________istream &operator >> (istream &in, Plane3D &p){  char dummy[25];			     in >> dummy >> p.normal;  in >> dummy >> p.offset;  return in;}  //==== operator << for Plane3D ====»aÿì€␀|¦N€␀ |¦¿áÿü␀ƒâø”!ÿÄ€␀€ƒ␀␀¨„␀$,␀␀A‚␀Kûga€A␀€␀€c␀␀8!␀<»áÿü€␀|¦N€␀ |¦¿!ÿä␀ƒ¢øƒ"ì”!ÿ¬¨}␀;C␀␀€}␀ƒƒ␀␀H␀,8z␀␀3ZÿÿTc<||¨c␀`;cÿÿ;û␀␀€|␀>€c␀␀ÃúH␀␀;ÿÿÿ,␀␀A‚␀;Şÿÿˆ~␀␀|cu,␀ A‚ÿä€}␀*Td'>p„␀A‚␀(Tcÿ@‚␀ ¨}␀|Ğ␀@‚␀€}␀X|␀@€␀ƒı␀XØA‚␀¤}Bx8ù␀¬8Û␀␀|¥*x8Ÿ␀␀€|␀>Kûeù€A␀€}␀ƒƒ␀␀€}␀*Tc'>pc␀A‚␀4€}␀T|ø␀@␀€}␀T|{}␀T€}␀X|ø␀@␀€}␀X|{}␀X¨|␀ |ø␀@␀¨|␀ d4|d°|␀ ¨|␀"|ø␀@␀¨|␀"d4|d°|␀",␀␀@‚şÔ€}␀Kûeõ€A␀8!␀T»!ÿä€␀|¦N€␀ |¦¿áÿü␀ƒâø”!ÿ¼€␀␀8c␀€␀␀␀8€␀␀<8a␀8KûeÁ€A␀8 ␀8…␀␀8a␀8KûeÅ€A␀8a␀8€Ÿ␀€„␀␀€␀␀␀€␀␀¨a␀<¨Ÿ␀¨¿␀|„)Ö|d°a␀88a␀8€Ÿ␀€„␀␀€␀␀␀␀€␀␀8!␀D»áÿü€␀|¦N€␀ |¦¿Áÿø␀␀;ã␀␀ƒÂø”!ÿ¸€a␀d8ƒ␀8d␀␀KûY‘€A␀€~␀D€␀dd␀␀(␀␀A‚␀ˆ¨␀l¨␀<| ␀@‚␀x8␀␀H␀␀Õ8␀␀KûYq€A␀€␀zKûe€A␀(␀␀@‚␀L8␀€␀␀␀8€␀␀<8␀␀Kûdõ€A␀8a␀8Kûe€A␀€␀8a␀8Kûe	€A␀8␀␀Kûe€A␀“ş␀D8!␀H»Áÿø€␀|¦N€␀ |¦¿áÿü␀;ã␀␀”!ÿÄ€␀␀€‚ød␀D(␀␀A‚␀€␀␀H␀␀%€␀KûXÁ€A␀8!␀<»áÿü€␀|¦N€␀ BøŠ␀␀|`@A‚␀t(␀␀A‚␀4Œ␀˜Œ␀␀9j␀␀„␀␀”␀␀8␀␀
